const ClipboardAction=require("./clipboard-action"),delegate=require("delegate-events"),Emitter=require("tiny-emitter");class Clipboard extends Emitter{constructor(selector){super(),delegate.bind(document.body,selector,"click",e=>this.initialize(e))}initialize(e){this.clipboardAction&&(this.clipboardAction=null),this.clipboardAction=new ClipboardAction({action:e.delegateTarget.getAttribute("data-action"),target:e.delegateTarget.getAttribute("data-target"),text:e.delegateTarget.getAttribute("data-text"),trigger:e.delegateTarget,host:this})}}export default Clipboard;global.Clipboard=Clipboard;